mapper.select:
SELECT id, name, species, age, description, gender, vaccinated, neutered, arrival_date AS arrivalDate, likes, dislikes FROM animal WHERE 1=1
AND (name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
AND species = #{species}
ORDER BY id DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM animal WHERE 1=1
AND (name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))
AND species = #{species}
