mapper.update:
UPDATE board
SET b_title = #{bTitle},
    b_content = #{bContent}
WHERE b_no = #{bNo}

UPDATE board
SET view_count = view_count + 1
WHERE b_no = #{bNo}

UPDATE board
SET like_count = like_count + 1
WHERE b_no = #{bNo}

UPDATE board
SET like_count = like_count + 1
WHERE b_no = #{bNo}

UPDATE board
SET like_count = CASE WHEN like_count > 0 THEN like_count - 1 ELSE 0 END
WHERE b_no = #{bNo}
